#include <iostream>

using namespace std;

int main() {
    // Declare and initialize variables
    int num1 = 5;
    int num2 = 10;

    // Calculate the sum of the two numbers
    int sum = num1 + num2;

    // Print the sum
    cout << "The sum of " << num1 << " and " << num2 << " is: " << sum << endl;

    return 0;
}
